$(function(){var $a = $('#width'),$b = $('#height'),$c=$('#password'),$d = $('#calculate');$d.click(function(){if(!validate('#width') || !validate('#height') || !validate('#password')) return;});$a.focusout(function(){if(!validate('#width'))$a.select();});$b.focusout(function(){if(!validate('#height'))$b.select();});$c.focusout(function(){if(!validate('#password'))$c.select();});function validate(field){var $e=$(field),$f=$(field + '-validation-msg');if(field=='#width'){if($e.val()==''){console.log('null');$f.html('用户名不能为空！');$e.select();return false;}$f.html('');return true;}else if(field=='#height'){if($e.val()==''){console.log('null');$f.html('手机号不能为空！');$e.select();return false;}else if(!(/^1(3|4|5|6|7|8|9)\d{9}$/.test($e.val()))){ $f.html('手机号格式不正确！');$e.select();return false;}$f.html('');return true;}else if(field=='#password'){if($e.val()==''){console.log('null');$f.html('密码设置不符合要求！');$e.select();return false;}$f.html('');return true;}}});